var actuator;
var value;

actuator = msg.topic;
value = msg.payload;



msg.payload = `{"actuator":"${actuator}", "value":"${value}"}"`
return msg;

var device = msg.MQTTtopic;

var sensor = msg.payload.sensor; 
var value = msg.payload.value;

msg.topic = `USE sensors; INSERT INTO Readings(device, datetime, sensor, value) VALUES('${device}', NOW(), '${sensor}', '${value}');`
return msg;

var timeStampStart = msg.startingDate + msg.startingTime;
var timeStampEnd = msg.endDate + msg.endTime;

var dateStart = new Date(timeStampStart);
var dateEnd = new Date(timeStampEnd);

var startQuery = dateStart.toISOString();
var endQuery = dateEnd.toISOString();

var sensor = msg.sensor;
var device = msg.device;

if (device === undefined){
    device = global.device;
}
global.device = device;

if (sensor === undefined){
    sensor = global.sensor;
}
global.sensor = sensor;

if (startQuery === undefined){
    startQuery = global.startDate;
}
global.startDate = startQuery;

if (endQuery === undefined) {
    endQuery = global.endDate;
}
global.endDate = endQuery;

msg.topic = `USE sensors; SELECT * FROM Readings WHERE device='${device}' AND sensor='${sensor}' AND datetime>='${startQuery}' AND datetime<'${endQuery}';`
return msg;
